import{_ as e,g as i}from"./app.efddb17c.js";const a={},l=i('<h1 id="\u8F6F\u4EF6\u7CFB\u7EDF\u8BBE\u8BA1\u539F\u5219" tabindex="-1"><a class="header-anchor" href="#\u8F6F\u4EF6\u7CFB\u7EDF\u8BBE\u8BA1\u539F\u5219" aria-hidden="true">#</a> \u8F6F\u4EF6\u7CFB\u7EDF\u8BBE\u8BA1\u539F\u5219</h1><h3 id="\u4E00-\u5355\u4E00\u804C\u8D23" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u5355\u4E00\u804C\u8D23" aria-hidden="true">#</a> \u4E00 \u5355\u4E00\u804C\u8D23</h3><h4 id="\u5B9A\u4E49" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49" aria-hidden="true">#</a> \u5B9A\u4E49</h4><p>SRP (Single Responsibility Principle) , &quot;There should never be more than one reason for aclass to change.&quot;</p><h4 id="\u5B9E\u8DF5" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u8DF5" aria-hidden="true">#</a> \u5B9E\u8DF5</h4><h3 id="\u4E8C-\u91CC\u6C0F\u66FF\u6362" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u91CC\u6C0F\u66FF\u6362" aria-hidden="true">#</a> \u4E8C \u91CC\u6C0F\u66FF\u6362</h3><h4 id="\u5B9A\u4E49-1" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49-1" aria-hidden="true">#</a> \u5B9A\u4E49</h4><p>Liskov Substitution Principle,LSP</p><ol><li>If for each object o1 of type S there is an object o2 of type T such that for all programs P defined in terms of T, the behavior of P is unchanged when o1 is substituted for o2 then S is a subtype of T. \u5982\u679C\u5BF9\u6BCF\u4E00\u4E2A\u7C7B\u578B\u4E3AS\u7684\u5BF9\u8C61o1\uFF0C\u90FD\u6709\u7C7B\u578B\u4E3AT\u7684\u5BF9\u8C61o2\u65F6\uFF0C\u7A0B\u5E8FP\u7684\u884C\u4E3A\u6CA1\u6709\u53D1\u751F\u53D8\u5316\uFF0C\u90A3\u4E48\u7C7B\u578BS\u662F\u7C7B\u578BT\u7684\u5B57\u7C7B\u578B\u3002</li><li>Functions that use pointers or references to base classes must be able to use objects of derived classes without knowing it. \u6240\u6709\u5F15\u7528\u57FA\u7C7B\u7684\u5730\u65B9\u5FC5\u987B\u80FD\u900F\u660E\u7684\u4F7F\u7528\u5176\u5B50\u7C7B\u7684\u5BF9\u8C61\u3002</li></ol><h4 id="\u5B9E\u8DF5-1" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u8DF5-1" aria-hidden="true">#</a> \u5B9E\u8DF5</h4><h3 id="\u4E09-\u4F9D\u8D56\u5012\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u4F9D\u8D56\u5012\u7F6E" aria-hidden="true">#</a> \u4E09 \u4F9D\u8D56\u5012\u7F6E</h3><h4 id="\u5B9A\u4E49-2" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49-2" aria-hidden="true">#</a> \u5B9A\u4E49</h4><p>Dependence Inversion Principle\uFF0CDIP. High level modules should not depend upon low level modules.Both should depend upon abstractions.Abstractions should not depend upon details.Details should depend upon abstractions.</p><p>\u4E09\u5C42\u542B\u4E49\uFF1A</p><ol><li>\u9AD8\u5C42\u6A21\u5757\u4E0D\u5E94\u8BE5\u4F9D\u8D56\u4F4E\u5C42\u6A21\u5757\uFF0C\u4E24\u8005\u90FD\u5E94\u8BE5\u4F9D\u8D56\u5176\u62BD\u8C61\uFF1B</li><li>\u62BD\u8C61\u4E0D\u5E94\u8BE5\u4F9D\u8D56\u7EC6\u8282\uFF1B</li><li>\u7EC6\u8282\u5E94\u8BE5\u4F9D\u8D56\u62BD\u8C61</li></ol><p>\u5728Java\u8BED\u8A00\u4E2D\u7684\u8868\u73B0\uFF1A</p><ol><li>\u6A21\u5757\u95F4\u7684\u4F9D\u8D56\u901A\u8FC7\u62BD\u8C61\u53D1\u751F\uFF0C\u5B9E\u73B0\u7C7B\u4E4B\u95F4\u4E0D\u53D1\u751F\u76F4\u63A5\u7684\u4F9D\u8D56\u5173\u7CFB\uFF0C\u5176\u4F9D\u8D56\u5173\u7CFB\u662F\u901A\u8FC7\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B\u4EA7\u751F\u7684\uFF1B</li><li>\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B\u4E0D\u4F9D\u8D56\u4E8E\u5B9E\u73B0\u7C7B\uFF1B</li><li>\u5B9E\u73B0\u7C7B\u4F9D\u8D56\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B</li></ol><p>\u603B\u7ED3\u5373\u4E3A\uFF0C\u9762\u5411\u63A5\u53E3\u7F16\u7A0B-OOD(Object-Oriented Design \u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1)</p><h4 id="\u5B9E\u8DF5-2" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u8DF5-2" aria-hidden="true">#</a> \u5B9E\u8DF5</h4><p>\u4E09\u79CD\u5199\u6CD5</p><ol><li>\u6784\u9020\u51FD\u6570\u4F20\u9012\u4F9D\u8D56\u5BF9\u8C61</li><li>Setter\u65B9\u6CD5\u4F20\u9012\u4F9D\u8D56\u5BF9\u8C61</li><li>\u63A5\u53E3\u58F0\u660E\u4F9D\u8D56\u5BF9\u8C61</li></ol><p>\u6700\u4F73\u5B9E\u8DF5</p><ol><li>\u6BCF\u4E2A\u7C7B\u5C3D\u91CF\u90FD\u6709\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B\uFF0C\u6216\u8005\u62BD\u8C61\u7C7B\u548C\u63A5\u53E3\u4E24\u8005\u90FD\u5177\u5907</li><li>\u53D8\u91CF\u7684\u8868\u9762\u7C7B\u578B\u5C3D\u91CF\u662F\u63A5\u53E3\u6216\u8005\u62BD\u8C61\u7C7B</li><li>\u4EFB\u4F55\u7C7B\u90FD\u4E0D\u5E94\u8BE5\u4ECE\u5177\u4F53\u7C7B\u6D3E\u751F</li><li>\u5C3D\u91CF\u4E0D\u8981\u8986\u5199\u57FA\u7C7B\u7684\u65B9\u6CD5</li><li>\u89E3\u548C\u91CC\u6C0F\u66FF\u6362\u539F\u5219\u4F7F\u7528</li></ol><h3 id="\u56DB-\u63A5\u53E3\u9694\u79BB" tabindex="-1"><a class="header-anchor" href="#\u56DB-\u63A5\u53E3\u9694\u79BB" aria-hidden="true">#</a> \u56DB \u63A5\u53E3\u9694\u79BB</h3><h4 id="\u5B9A\u4E49-3" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49-3" aria-hidden="true">#</a> \u5B9A\u4E49</h4><ol><li>Clients should not be forced to depend upon interfaces that they don&#39;t use. \u5BA2\u6237\u7AEF\u4E0D\u5E94\u8BE5\u4F9D\u8D56\u5B83\u4E0D\u9700\u8981\u7684\u63A5\u53E3</li><li>The dependency of one class to another one should depend on the smallest possible interface. \u7C7B\u95F4\u7684\u4F9D\u8D56\u5173\u7CFB\u5E94\u8BE5\u5EFA\u7ACB\u5728\u6700\u5C0F\u7684\u63A5\u53E3\u4E0A</li></ol><h3 id="\u4E94-\u8FEA\u7C73\u7279\u6CD5\u5219" tabindex="-1"><a class="header-anchor" href="#\u4E94-\u8FEA\u7C73\u7279\u6CD5\u5219" aria-hidden="true">#</a> \u4E94 \u8FEA\u7C73\u7279\u6CD5\u5219</h3><h4 id="\u5B9A\u4E49-4" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49-4" aria-hidden="true">#</a> \u5B9A\u4E49</h4><p>Law of Demeter(LoD)\u4E5F\u79F0\u4E3A\u6700\u5C11\u77E5\u8BC6\u539F\u5219\uFF08Least Knowledge Principle, LKP\uFF09</p><p>\u4E00\u4E2A\u5BF9\u8C61\u5E94\u8BE5\u5BF9\u5176\u4ED6\u5BF9\u8C61\u6709\u6700\u5C11\u7684\u4E86\u89E3</p><ol><li>\u53EA\u548C\u670B\u53CB\u4EA4\u6D41</li><li>\u670B\u53CB\u95F4\u4E5F\u662F\u6709\u8DDD\u79BB\u7684</li><li>\u662F\u81EA\u5DF1\u7684\u5C31\u662F\u81EA\u5DF1\u7684\uFF1A \u5982\u679C\u4E00\u4E2A\u65B9\u6CD5\u653E\u5728\u672C\u7C7B\u4E2D\uFF0C\u5373\u4E0D\u589E\u52A0\u7C7B\u95F4\u5173\u7CFB\uFF0C\u4E5F\u5BF9\u672C\u7C7B\u4E0D\u4EA7\u751F\u8D1F\u9762\u5F71\u54CD\uFF0C\u5C31\u653E\u7F6E\u5728\u672C\u7C7B\u4E2D</li><li>\u8C28\u614E\u4F7F\u7528Serializable\uFF1A</li></ol><h3 id="\u516D-\u5F00\u95ED\u539F\u5219" tabindex="-1"><a class="header-anchor" href="#\u516D-\u5F00\u95ED\u539F\u5219" aria-hidden="true">#</a> \u516D \u5F00\u95ED\u539F\u5219</h3><h4 id="\u5B9A\u4E49-5" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49-5" aria-hidden="true">#</a> \u5B9A\u4E49</h4><p>Software entities like classes, modules and functions should be open for extension but closed for modifications.</p><p>\u4E00\u4E2A\u8F6F\u4EF6\u5B9E\u4F53\u5982\u7C7B\u3001\u6A21\u5757\u548C\u51FD\u6570\u5E94\u8BE5\u5BF9\u6269\u5C55\u5F00\u53D1\uFF0C\u5BF9\u4FEE\u6539\u5173\u95ED</p><h4 id="\u5B9E\u8DF5-3" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u8DF5-3" aria-hidden="true">#</a> \u5B9E\u8DF5</h4><p>\u53D8\u5316\u7684\u4E09\u79CD\u7C7B\u578B</p><ol><li>\u903B\u8F91\u53D8\u5316</li><li>\u5B50\u6A21\u5757\u53D8\u5316</li><li>\u53EF\u89C1\u8BD5\u56FE\u53D8\u5316</li></ol><p>\u7406\u89E3\u5F00\u95ED\u539F\u5219\u7684\u91CD\u8981\u6027\u7684\u51E0\u4E2A\u65B9\u9762</p><ol><li>\u5F00\u95ED\u539F\u5219\u5BF9\u6D4B\u8BD5\u7684\u5F71\u54CD</li><li>\u5F00\u95ED\u539F\u5219\u53EF\u4EE5\u63D0\u9AD8\u590D\u7528\u6027</li><li>\u5F00\u95ED\u539F\u5219\u53EF\u4EE5\u63D0\u9AD8\u53EF\u7EF4\u62A4\u6027</li><li>\u9762\u5411\u5BF9\u8C61\u5F00\u53D1\u7684\u8981\u6C42</li></ol><h4 id="\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#\u5E94\u7528" aria-hidden="true">#</a> \u5E94\u7528</h4><ol><li>\u62BD\u8C61\u7EA6\u675F</li><li>\u5143\u6570\u636E\uFF08metadata\uFF09\u63A7\u5236\u6A21\u5757\u884C\u4E3A</li><li>\u6307\u5B9A\u9879\u76EE\u7AE0\u7A0B</li><li>\u5C01\u88C5\u53D8\u5316</li></ol>',42);function h(r,d){return l}var n=e(a,[["render",h],["__file","design-philosophy.html.vue"]]);export{n as default};
